    private void MateriaPrimaGUI(){
        try{
            
            this.mp = this.materiaprima_servicio.recuperarTodas(Conexion.obtener());
            String solicitar = null;
            DefaultTableModel dtm = (DefaultTableModel) jTable1.getModel();
            dtm.setRowCount(0);
            for(int i = 0; i < this.mp.size(); i++){
                dtm.addRow(new Object[]{
                    this.mp.get(i).getCalibre(),
                    this.mp.get(i).getConsumoR(),
                    this.mp.get(i).getInventarioF(),
                    this.mp.get(i).getInventarioA(),
                    this.mp.get(i).getSolicitudTransito(),
                    this.mp.get(i).getKgSolicitar(),
                    this.mp.get(i).getAprobacion(), 
                });  
            }
            
        }catch(SQLException ex){
            System.out.println(ex.getMessage());
            JOptionPane.showMessageDialog(this, "Ha surgido un error y no se han podido recuperar los registros");
        }catch(ClassNotFoundException ex){
            System.out.println(ex);
            JOptionPane.showMessageDialog(this, "Ha surgido un error y no se han podido recuperar los registros");
        }        
    }
    
