                ResultSet rs = st.executeQuery(sql);
                if(rs.next()== true){
                      MATINPDF();
                      JOptionPane.showMessageDialog(this, "¡ORDEN DE MATIN GENERADA!");
                }
  
                String sql1 = "SELECT materia_prima.calibre FROM materia_prima, proveedores WHERE materia_prima.calibre=proveedores.calibre AND proveedores.nombre='B.F STEEL DE MEXICO S.A DE C.V' AND aprobacion='SOLICITAR'";
                ResultSet rs1 = st.executeQuery(sql1);
                if(rs1.next()== true){
                    BFSteelPDF();
                    JOptionPane.showMessageDialog(this, "¡ORDEN DE B.F STEEL GENERADA!");
                }
                
                String sql3 = "SELECT materia_prima.calibre FROM materia_prima, proveedores WHERE materia_prima.calibre=proveedores.calibre AND proveedores.nombre='Aceros Especiales Magaña' AND aprobacion='SOLICITAR'";
